import { Component, OnInit } from '@angular/core';
import { Person } from '../person.model';
import { PersonService } from '../person.service';

@Component({
  selector: 'app-person-list',
  templateUrl: './person-list.component.html',
  styleUrls: ['./person-list.component.css']
})
export class PersonListComponent implements OnInit {
  people!: Person[];

  constructor(private personService: PersonService) {}

  ngOnInit() {
    this.people = this.personService.getAllPeople();
  }

  editPerson(id: number): void {
    // Implement the logic to navigate to the person edit view
  }

  deletePerson(id: number): void {
    this.personService.deletePerson(id);
    this.people = this.personService.n();
  }
}
<h2>People List</h2>
<table>
  <thead>
    <tr>
      <th>ID</th>
      <th>Name</th>
      <th>Age</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let person of people">
      <td>{{ person.id }}</td>
      <td>{{ person.name }}</td>
      <td>{{ person.age }}</td>
      <td>
        <button (click)="editPerson(person.id)">Edit</button>
        <button (click)="deletePerson(person.id)">Delete</button>
      </td>
    </tr>
  </tbody>
</table>
