import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Person } from '../person.model';
import { PersonService } from '../person.service';

@Component({
  selector: 'app-person-edit',
  templateUrl: './person-edit.component.html',
  styleUrls: ['./person-edit.component.css']
})
export class PersonEditComponent implements OnInit {
  person: Person = this.person.age;

  constructor(private route: ActivatedRoute, private personService: PersonService) {}

  ngOnInit() {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    const person = this.personService.getPersonById(id);

    if (person) {
      this.person = person;
    } else {
      // Handle the case when the person is not found
      // For example, you can navigate back to the person list view or display an error message
    }
  }

  savePerson(): void {
    this.personService.updatePerson(this.person);
    // Implement the logic to navigate back to the person list view
  }
}
<h2>Edit Person</h2>
<div *ngIf="person">
  <label>Name: </label>
  <input [(ngModel)]="person.name" placeholder="Enter name" /><br />
  <label>Age: </label>
  <input [(ngModel)]="person.age" placeholder="Enter age" /><br />
  <button (click)="savePerson()">Save</button>
</div>
