import { Component, OnInit } from '@angular/core';
import { Person } from '../person.model';
import { ApiService } from '../api.service';

@Component({
  selector: 'app-person-list',
  templateUrl: './person-list.component.html',
  styleUrls: ['./person-list.component.css']
})
export class PersonListComponent implements OnInit {
  people!: Person[];

  constructor(private apiService: ApiService) {}

  ngOnInit() {
    this.apiService.getAllPeople().subscribe(
      (people: Person[]) => {
        this.people = people;
      },
      (error) => {
        console.error('Error fetching people:', error);
      }
    );
  }

  editPerson(id: number): void {
    // Implement the logic to navigate to the person edit view
  }

  deletePerson(id: number): void {
    this.apiService.deletePerson(id).subscribe(
      () => {
        this.people = this.people.filter(person => person.id !== id);
      },
      (error) => {
        console.error('Error deleting person:', error);
      }
    );
  }
}
<h2>People List</h2>
<ul>
  <li *ngFor="let person of people">
    {{ person.name }} ({{ person.age }} years old)
    <button (click)="editPerson(person.id)">Edit</button>
    <button (click)="deletePerson(person.id)">Delete</button>
  </li>
</ul>
